CREATE USER clases IDENTIFIED BY "Herrera2024_" DEFAULT TABLESPACE DATA QUOTA 50M ON DATA ;
GRANT CONNECT, RESOURCE, CREATE VIEW TO clases;

create table clases.empresas(
   cif char(9) constraint empresas_pk primary key,
   nombre varchar(25) constraint empresas_uk unique 
           constraint empresas_nn not null,
   direccion varchar(50),
   telefono char(9)
);

create table clases.alumnos(
   nif char(9) constraint alumnos_pk primary key,
   nombre varchar(50) constraint alumnos_nn1 not null,
   apellido1 varchar(50) constraint alumnos_nn2 not null,
   apellido2 varchar(50),
   direccion varchar(50),
   telefono char(9),
   edad number(2),
   cif char(9) constraint alumnos_fk references clases.empresas
          on delete set null
);

create table clases.profesores(
   nif char(9) constraint profesores_pk primary key,
   nombre varchar2(50) constraint profesores_nn1 not null,
   apellido1 varchar2(50) constraint profesores_nn2 not null,
   apellido2 varchar2(50),
   direccion varchar2(50),
   telefono char(9)
);

create table clases.tipos_curso(
  cod_curso char(8) constraint tipos_curso_pk primary key,
  titulo varchar2(50) constraint tipos_curso_uk unique,
  duracion number(3),
  temario clob,
  materias varchar2(100)
);
  

create table clases.cursos(
   n_curso number(3) constraint cursos_pk primary key,
   fecha_inicio timestamp(0),
   fecha_fin timestamp(0),
   cod_curso char(8) constraint cursos_fk1
       references clases.tipos_curso
     on delete set null,
   nif_profesor char(9) constraint cursos_fk2
       references clases.profesores
         on delete set null
);


create table clases.asistir(
   nif char(9) constraint asistir_fk1 references clases.alumnos
       on delete cascade,
   n_curso number(3) constraint asistir_fk2 references clases.cursos
       on delete cascade,
   nota number(4,2),
   constraint asistir_pk primary key(nif,n_curso)
)








