
CREATE USER FUTBOL IDENTIFIED BY "Herrera2024_" DEFAULT TABLESPACE DATA QUOTA 50M ON DATA ;
GRANT CONNECT, RESOURCE, CREATE VIEW, CREATE MATERIALIZED VIEW TO FUTBOL;

CREATE TABLE futbol.equipos(
  id_equipo NUMBER(2) CONSTRAINT equipos_pk PRIMARY KEY,
  nombre VARCHAR2(50) CONSTRAINT equipos_uk1 UNIQUE
     CONSTRAINT equipos_nn1 NOT NULL,
  estadio VARCHAR2(50) CONSTRAINT equipos_uk2 UNIQUE,
  aforo NUMBER(6),
  ano_fundacion NUMBER(4),
  ciudad VARCHAR2(50) CONSTRAINT equipos_nn2 NOT NULL
);

CREATE TABLE futbol.jugadores(
   id_jugador NUMBER(3) CONSTRAINT jugadores_pk PRIMARY KEY,
   nombre VARCHAR2(50) CONSTRAINT jugadores_nn NOT NULL,
   fecha_nac DATE,
   id_equipo NUMBER(2) CONSTRAINT jugadores_fk REFERENCES futbol.equipos
      ON DELETE SET NULL
);

CREATE TABLE futbol.partidos(
   id_equipo_casa NUMBER(2) CONSTRAINT partidos_fk1 REFERENCES futbol.equipos
        ON DELETE CASCADE,
   id_equipo_fuera NUMBER(2) CONSTRAINT partidos_fk2
        REFERENCES futbol.EQUIPOS ON DELETE CASCADE,
   fecha TIMESTAMP,
   goles_casa NUMBER(2),
   goles_fuera NUMBER(2),
   observaciones VARCHAR2(2000),
   CONSTRAINT partidos_pk
      PRIMARY KEY(id_equipo_casa, id_equipo_fuera),
   CONSTRAINT partidos_ck CHECK(id_equipo_casa!=id_equipo_fuera)
);

CREATE TABLE futbol.goles(
   id_equipo_casa NUMBER(2),
   id_equipo_fuera NUMBER(2),
   minuto INTERVAL DAY TO SECOND,
   descripcion VARCHAR2(2000),
   id_jugador NUMBER(3) CONSTRAINT goles_fk2 REFERENCES futbol.jugadores ON DELETE CASCADE,
   CONSTRAINT goles_fk1 FOREIGN KEY(id_equipo_casa, id_equipo_fuera)
         REFERENCES futbol.partidos(id_equipo_casa, id_equipo_fuera)
            ON DELETE CASCADE,
   CONSTRAINT goles_pk
            PRIMARY KEY(id_equipo_casa, id_equipo_fuera,minuto)
);



